CREATE TABLE IF NOT EXISTS guilds(guildid TEXT PRIMARY KEY UNIQUE NOT NULL, guildname TEXT NOT NULL, picture TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS guildmembers(userid TEXT NOT NULL, guildid TEXT REFERENCES guilds(guildid));
CREATE TABLE IF NOT EXISTS users(id TEXT PRIMARY KEY UNIQUE NOT NULL, email TEXT UNIQUE NOT NULL, username TEXT NOT NULL , avatar TEXT NOT NULL, password TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS invites(inviteid TEXT PRIMARY KEY UNIQUE, guildid TEXT REFERENCES guilds(guildid));
CREATE TABLE IF NOT EXISTS messages(messageid TEXT PRIMARY KEY UNIQUE, guildid TEXT REFERENCES guilds(guildid), channelid TEXT REFERENCES channels(channelid), author TEXT REFERENCES users(id), createdat INTEGER NOT NULL, message TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS channels(channelid TEXT PRIMARY KEY UNIQUE, guildid TEXT REFERENCES guilds(guildid), channelname TEXT);
